sp_widget.client_script:
api.controller=function($rootScope, $scope) {
	/* widget controller */
	var c = this;
	c.selectItem = function(idx){
		var id = c.data.notes[idx].sys_id;
		//console.log("Note ID = " + id);
		$rootScope.noteID = id;//joga o id no obj pai
		$rootScope.$emit('selectNote', id); //emite o evento 'selectNode'
	}
};

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */

	//Create an array to popolate with notes
	data.notes = [];
	var noteLists = new GlideRecord('x_snc_createnotes_note');
	noteLists.addEncodedQuery("user=" + gs.getUserID());
	noteLists.orderByDesc("sys_created_on")
	noteLists.query();
	while(noteLists.next()){
		var noteObj = {};

		$sp.getRecordDisplayValues(noteObj, noteLists, 'number,title,sys_id,sys_created_on');
		noteObj.note = noteLists.getValue('note').substr(0,20);

		data.notes.push(noteObj);
	}


})();
